#!/bin/bash
# Dete:2019/09/12
# Description:添加用户公钥到~/.ssh/authorized_keys
PATH=/usr/local/sbin:/usr/local/bin:/sbin:/bin:/usr/sbin:/usr/bin

user="$1"
group="$2"
user_sshkey="${user}_sshkey"

hujf_sshkey="ssh-dss "
huangzhx_sshkey="ssh-dss "
sheab_sshkey="ssh-dss "
zhangmsh_sshkey="ssh-dss "
yejq_sshkey="ssh-dss "
hexing_sshkey="ssh-dss "
zhuoqh_sshkey="ssh-dss "
huangdw_sshkey="ssh-dss "
wujf_sshkey="ssh-dss "
chenpy_sshkey="ssh-dss "
xiaolq_sshkey="ssh-dss "
zengjx_sshkey="ssh-dss "
lizhy_sshkey="ssh-dss "
chengshj_sshkey="ssh-dss "
linjq_sshkey="ssh-dss "
chenyi_sshkey="ssh-dss "
chenjx_sshkey="ssh-dss "
linhy_sshkey="ssh-dss "

# 判断脚本传参，打印脚本用法
[[ "$#" -ne 2 ]] && {
	echo "Wrong number of argvs"
	echo "Usage: $0 {hujf|xiedh...} {admin|super|tomcat|ftp}"
	exit 1
}

# 检查用户是否在上文的提供范围内
[[ -z ${!user_sshkey} ]] && {
	echo "Warning:Undefine user"
	exit 1
}

# 限定group组只能为super|ftp|tomcat|admin
[[ ! "$group" =~ ^(admin|super|tomcat|ftp)$ ]] && {
	echo "Warning:Undefine group"
	exit 1
}

# 限制admin只能添加hujf
[[ "$group" == "admin" && "$user" != "hujf" ]] && {
	echo "Warning:Only user of hujf can add in group of admin" 
	exit 1
}

# 检查用户组是否已存在
getent group $group &> /dev/null || {
	echo "Warning:group $group does not exists on system"
	exit 1
}

# 检查用户是否已存在
if id $user &> /dev/null;then
	usermod -G $group $user
else
	useradd $user -g $group
fi
mkdir -p /home/$user/.ssh
chown $user:$group /home/$user/.ssh
chmod 700 /home/$user/.ssh
echo "${!user_sshkey}" > /home/$user/.ssh/authorized_keys
echo "addkey $user sucess!"
